import React, { useState } from 'react';
import { Route, Switch, useLocation, useHistory, BrowserRouter } from 'react-router-dom';
 import Header from '../Header/Header';
 import Main from '../Main/Main';
  import SavedNews from '../SavedNews/SavedNews';
 import Footer from '../Footer/Footer';
 import './App.css'; // we will usually place CSS imports last




 function App() {

  const [Loggedin, setLoggedin] = useState(false);
  // Used to change header color when on saved news page
  const [AtSavedNews, setAtSavedNews] = useState(false);


  return (
     <>
      <div className="page">
       <Header 
       loggedIn={Loggedin}
       savedNews={SavedNews}
       />
       <BrowserRouter> 
         <Switch> 
          <Route exact path='/'>
            <Main

            />
          </Route>
          <Route path='/saved-news'>
            <SavedNews/>
          </Route> 
        </Switch>
</BrowserRouter>

       <Footer />
       </div>
     </>
   );
 }

export default App;
