import author from '../../images/Desi.jpg';

function About(props) {
    return (
        (
            <section className='about'>
                <img className='about__image' src={author} alt='the author' />
                <div className='about__container'>
                    <h2 className='about__heading'>Desiree Joy</h2>
                    <p className='about__description'>Desiree Joy Bradish is a full stack bootcamp graduate of Practicum by Yandex. She is proficient in the MERN stack and is eager to focus on accesibility on the web.</p>
                </div>
            </section>
        )
    )
}

export default About;
